import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import multer from "multer";
import path from "path";
import fs from "fs";
import { storage } from "./storage";
import { setupAuth, isAuthenticated, hashPassword, validateRegister, validateLogin } from "./auth";
import passport from "passport";
import { insertResultSchema, insertPollingCenterSchema, insertCandidateSchema } from "@shared/schema";
import { seedDatabase } from "./seed";

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), "uploads");
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const upload = multer({
  dest: uploadDir,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = /jpeg|jpg|png|pdf/;
    const extname = allowedTypes.test(path.extname(file.originalname).toLowerCase());
    const mimetype = allowedTypes.test(file.mimetype);

    if (mimetype && extname) {
      return cb(null, true);
    } else {
      cb(new Error("Only images (JPEG, JPG, PNG) and PDF files are allowed"));
    }
  },
});

// WebSocket connections store
const wsConnections = new Set<WebSocket>();

// Broadcast real-time updates to all connected clients
function broadcastUpdate(type: string, data: any) {
  const message = JSON.stringify({
    type,
    data,
    timestamp: new Date().toISOString(),
  });

  wsConnections.forEach((ws) => {
    if (ws.readyState === WebSocket.OPEN) {
      ws.send(message);
    }
  });
}

// Analytics helper to get real-time stats
async function getRealTimeAnalytics() {
  try {
    const stats = await storage.getStats();
    const recentSubmissions = await storage.getRecentSubmissions(10);
    const verificationQueue = await storage.getPendingVerifications();
    const topPerformingCenters = await storage.getTopPerformingCenters(5);
    const hourlySubmissions = await storage.getHourlySubmissionTrends();

    return {
      overview: stats,
      recentActivity: recentSubmissions,
      pendingVerifications: verificationQueue.length,
      topCenters: topPerformingCenters,
      submissionTrends: hourlySubmissions,
      lastUpdated: new Date().toISOString(),
    };
  } catch (error) {
    console.error('Error getting real-time analytics:', error);
    return null;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Seed database on startup
  await seedDatabase();

  // Admin routes
  app.get('/api/admin/pending-users', isAuthenticated, async (req: any, res) => {
    if (req.user?.role !== 'admin') {
      return res.status(403).json({ message: "Admin access required" });
    }
    try {
      const pendingUsers = await storage.getPendingUsers();
      res.json(pendingUsers);
    } catch (error) {
      console.error("Error fetching pending users:", error);
      res.status(500).json({ message: "Failed to fetch pending users" });
    }
  });

  app.post('/api/admin/approve-user/:userId', isAuthenticated, async (req: any, res) => {
    if (req.user?.role !== 'admin') {
      return res.status(403).json({ message: "Admin access required" });
    }
    try {
      const { userId } = req.params;
      const approvedUser = await storage.approveUser(userId);
      res.json(approvedUser);
    } catch (error) {
      console.error("Error approving user:", error);
      res.status(500).json({ message: "Failed to approve user" });
    }
  });

  // Auth routes
  app.post('/api/register', async (req, res) => {
    try {
      const userData = validateRegister(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByIdentifier(userData.email || userData.phone || '');
      if (existingUser) {
        return res.status(400).json({ message: "User already exists with this email or phone" });
      }

      // Hash password and create user
      const passwordHash = await hashPassword(userData.password);
      const user = await storage.createUser({
        email: userData.email || undefined,
        phone: userData.phone || undefined,
        firstName: userData.firstName || '',
        lastName: userData.lastName || '',
        passwordHash,
      });

      // Remove password hash from response
      const { passwordHash: _, ...userResponse } = user;
      res.status(201).json(userResponse);
    } catch (error: any) {
      console.error("Registration error:", error);
      res.status(400).json({ message: error.message || "Registration failed" });
    }
  });

  app.post('/api/login', (req, res, next) => {
    try {
      validateLogin(req.body);

      passport.authenticate('local', (err: any, user: any, info: any) => {
        if (err) {
          return res.status(500).json({ message: "Login failed" });
        }
        if (!user) {
          return res.status(401).json({ message: info?.message || "Invalid credentials" });
        }

        req.logIn(user, (err) => {
          if (err) {
            return res.status(500).json({ message: "Login failed" });
          }
          // Remove password hash from response
          const { passwordHash, ...userResponse } = user;
          res.json(userResponse);
        });
      })(req, res, next);
    } catch (error: any) {
      res.status(400).json({ message: error.message || "Invalid request" });
    }
  });

  app.post('/api/logout', (req, res) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ message: "Logout failed" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user;
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      // Remove password hash from response
      const { passwordHash, ...userResponse } = user;
      res.json(userResponse);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard stats
  app.get("/api/stats", isAuthenticated, async (req, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching stats:", error);
      res.status(500).json({ message: "Failed to fetch statistics" });
    }
  });

  // Real-time analytics endpoint
  app.get("/api/analytics", isAuthenticated, async (req, res) => {
    try {
      const analytics = await getRealTimeAnalytics();
      if (analytics) {
        res.json(analytics);
      } else {
        res.status(500).json({ message: "Failed to fetch analytics" });
      }
    } catch (error) {
      console.error("Error fetching analytics:", error);
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  // Polling centers
  app.get("/api/polling-centers", isAuthenticated, async (req, res) => {
    try {
      const centers = await storage.getPollingCenters();
      res.json(centers);
    } catch (error) {
      console.error("Error fetching polling centers:", error);
      res.status(500).json({ message: "Failed to fetch polling centers" });
    }
  });

  app.post("/api/polling-centers", isAuthenticated, async (req: any, res) => {
    try {
      // Only admins can create polling centers
      const user = req.user;
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Access denied" });
      }

      const validatedData = insertPollingCenterSchema.parse(req.body);
      const center = await storage.createPollingCenter(validatedData);

      // Log audit
      await storage.createAuditLog({
        userId: req.user.id,
        action: "CREATE",
        entityType: "polling_center",
        entityId: center.id,
        newValues: center,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      res.status(201).json(center);
    } catch (error) {
      console.error("Error creating polling center:", error);
      res.status(400).json({ message: "Failed to create polling center" });
    }
  });

  // Candidates
  app.get("/api/candidates", isAuthenticated, async (req, res) => {
    try {
      const candidates = await storage.getCandidates();
      res.json(candidates);
    } catch (error) {
      console.error("Error fetching candidates:", error);
      res.status(500).json({ message: "Failed to fetch candidates" });
    }
  });

  app.post("/api/candidates", isAuthenticated, async (req: any, res) => {
    try {
      // Only admins can create candidates
      const user = req.user;
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Access denied" });
      }

      const validatedData = insertCandidateSchema.parse(req.body);
      const candidate = await storage.createCandidate(validatedData);
      res.status(201).json(candidate);
    } catch (error) {
      console.error("Error creating candidate:", error);
      res.status(400).json({ message: "Failed to create candidate" });
    }
  });

  // Results
  app.get("/api/results", isAuthenticated, async (req, res) => {
    try {
      const { status } = req.query;
      let results;

      if (status) {
        results = await storage.getResultsByStatus(status as any);
      } else {
        results = await storage.getResults();
      }

      res.json(results);
    } catch (error) {
      console.error("Error fetching results:", error);
      res.status(500).json({ message: "Failed to fetch results" });
    }
  });

  app.post("/api/results", isAuthenticated, upload.array('files', 10), async (req: any, res) => {
    try {
      const validatedData = insertResultSchema.parse({
        ...req.body,
        presidentialVotes: req.body.presidentialVotes ? JSON.parse(req.body.presidentialVotes) : null,
        mpVotes: req.body.mpVotes ? JSON.parse(req.body.mpVotes) : null,
        councilorVotes: req.body.councilorVotes ? JSON.parse(req.body.councilorVotes) : null,
        invalidVotes: parseInt(req.body.invalidVotes),
        submittedBy: req.user.id,
      });

      const result = await storage.createResult(validatedData);

      // Handle file uploads
      if (req.files && req.files.length > 0) {
        for (const file of req.files) {
          await storage.createResultFile({
            resultId: result.id,
            fileName: file.originalname,
            filePath: file.path,
            fileSize: file.size,
            mimeType: file.mimetype,
          });
        }
      }

      // Log audit
      await storage.createAuditLog({
        userId: req.user.id,
        action: "CREATE",
        entityType: "result",
        entityId: result.id,
        newValues: result,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      // Broadcast real-time updates
      broadcastUpdate("NEW_RESULT", result);

      // Get and broadcast updated analytics
      const analytics = await getRealTimeAnalytics();
      if (analytics) {
        broadcastUpdate("ANALYTICS_UPDATE", analytics);
      }

      res.status(201).json(result);
    } catch (error) {
      console.error("Error creating result:", error);
      res.status(400).json({ message: "Failed to create result" });
    }
  });

  // Verify/approve results
  app.patch("/api/results/:id/status", isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user;
      if (user?.role !== 'supervisor' && user?.role !== 'admin') {
        return res.status(403).json({ message: "Access denied. Supervisor or admin role required." });
      }

      const { status, flaggedReason } = req.body;
      const resultId = req.params.id;

      const updatedResult = await storage.updateResultStatus(
        resultId,
        status,
        req.user.id,
        flaggedReason
      );

      // Log audit
      await storage.createAuditLog({
        userId: req.user.id,
        action: "UPDATE",
        entityType: "result",
        entityId: resultId,
        newValues: { status, verifiedBy: req.user.id, flaggedReason },
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      // Broadcast real-time updates
      broadcastUpdate("RESULT_STATUS_CHANGED", updatedResult);

      // Get and broadcast updated analytics
      const analytics = await getRealTimeAnalytics();
      if (analytics) {
        broadcastUpdate("ANALYTICS_UPDATE", analytics);
      }

      res.json(updatedResult);
    } catch (error) {
      console.error("Error updating result status:", error);
      res.status(400).json({ message: "Failed to update result status" });
    }
  });

  // User management (admin only)
  app.get("/api/users", isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user;
      if (user?.role !== 'admin') {
        return res.status(403).json({ message: "Access denied" });
      }

      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.patch("/api/users/:id/role", isAuthenticated, async (req: any, res) => {
    try {
      const currentUser = await storage.getUser(req.user.id);
      if (currentUser?.role !== 'admin') {
        return res.status(403).json({ message: "Access denied" });
      }

      const { role } = req.body;
      const userId = req.params.id;

      const updatedUser = await storage.updateUserRole(userId, role);

      // Log audit
      await storage.createAuditLog({
        userId: req.user.id,
        action: "UPDATE",
        entityType: "user",
        entityId: userId,
        newValues: { role },
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      res.json(updatedUser);
    } catch (error) {
      console.error("Error updating user role:", error);
      res.status(400).json({ message: "Failed to update user role" });
    }
  });

  // Audit logs
  app.get("/api/audit-logs", isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user;
      if (user?.role !== 'admin' && user?.role !== 'supervisor') {
        return res.status(403).json({ message: "Access denied" });
      }

      const logs = await storage.getAuditLogs();
      res.json(logs);
    } catch (error) {
      console.error("Error fetching audit logs:", error);
      res.status(500).json({ message: "Failed to fetch audit logs" });
    }
  });

  // File serving
  app.get("/api/files/:filename", isAuthenticated, (req, res) => {
    const filename = req.params.filename;
    const filePath = path.join(uploadDir, filename);

    if (fs.existsSync(filePath)) {
      res.sendFile(filePath);
    } else {
      res.status(404).json({ message: "File not found" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket server for real-time updates
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  // Periodic analytics broadcast (every 30 seconds)
  setInterval(async () => {
    if (wsConnections.size > 0) {
      const analytics = await getRealTimeAnalytics();
      if (analytics) {
        broadcastUpdate('ANALYTICS_UPDATE', analytics);
      }
    }
  }, 30000);

  wss.on('connection', (ws) => {
    console.log('New WebSocket connection');
    wsConnections.add(ws);

    ws.on('message', async (message) => {
      try {
        const data = JSON.parse(message.toString());

        if (data.type === 'REQUEST_ANALYTICS') {
          const analytics = await getRealTimeAnalytics();
          if (analytics && ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({
              type: 'ANALYTICS_UPDATE',
              data: analytics,
              timestamp: new Date().toISOString(),
            }));
          }
        }
      } catch (error) {
        console.error('Error handling WebSocket message:', error);
      }
    });

    ws.on('close', () => {
      console.log('WebSocket connection closed');
      wsConnections.delete(ws);
    });

    ws.on('error', (error) => {
      console.error('WebSocket error:', error);
      wsConnections.delete(ws);
    });

    // Send initial analytics
    getRealTimeAnalytics().then(analytics => {
      if (analytics && ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify({
          type: "ANALYTICS_UPDATE",
          data: analytics,
          timestamp: new Date().toISOString(),
        }));
      }
    });
  });

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  pollingCenters,
  candidates,
  results,
  resultFiles,
  auditLogs,
  type User,
  type UpsertUser,
  type PollingCenter,
  type InsertPollingCenter,
  type Candidate,
  type InsertCandidate,
  type Result,
  type InsertResult,
  type ResultWithRelations,
  type ResultFile,
  type InsertResultFile,
  type AuditLog,
  type InsertAuditLog,
  type UserRole,
  type ResultStatus,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, or, count, sql } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByIdentifier(identifier: string): Promise<User | undefined>;
  createUser(user: { email?: string; phone?: string; firstName: string; lastName: string; passwordHash: string }): Promise<User>;
  upsertUser(user: UpsertUser): Promise<User>;
  getAllUsers(): Promise<User[]>;
  updateUserRole(userId: string, role: UserRole): Promise<User>;
  updateLastLogin(userId: string): Promise<void>;

  // Polling center operations
  getPollingCenters(): Promise<PollingCenter[]>;
  getPollingCenter(id: string): Promise<PollingCenter | undefined>;
  createPollingCenter(center: InsertPollingCenter): Promise<PollingCenter>;

  // Candidate operations
  getCandidates(): Promise<Candidate[]>;
  createCandidate(candidate: InsertCandidate): Promise<Candidate>;

  // Result operations
  getResults(): Promise<ResultWithRelations[]>;
  getResultsByStatus(status: ResultStatus): Promise<ResultWithRelations[]>;
  getResultsByPollingCenter(pollingCenterId: string): Promise<ResultWithRelations[]>;
  createResult(result: InsertResult): Promise<Result>;
  updateResultStatus(resultId: string, status: ResultStatus, verifiedBy?: string, flaggedReason?: string): Promise<Result>;

  // Result file operations
  createResultFile(file: InsertResultFile): Promise<ResultFile>;
  getResultFiles(resultId: string): Promise<ResultFile[]>;

  // Statistics
  getStats(): Promise<{
    totalCenters: number;
    resultsReceived: number;
    verified: number;
    flagged: number;
    completionRate: number;
    verificationRate: number;
  }>;

  // Analytics operations for real-time dashboard
  getRecentSubmissions(limit: number): Promise<any[]>;
  getPendingVerifications(): Promise<any[]>;
  getTopPerformingCenters(limit: number): Promise<Array<{
    pollingCenter: PollingCenter;
    submissionCount: number;
    verificationRate: number;
  }>>;
  getHourlySubmissionTrends(): Promise<Array<{
    hour: string;
    submissions: number;
    verifications: number;
  }>>;

  // Audit operations
  createAuditLog(log: InsertAuditLog): Promise<AuditLog>;
  getAuditLogs(limit?: number): Promise<AuditLog[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByIdentifier(identifier: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(
      or(eq(users.email, identifier), eq(users.phone, identifier))
    );
    return user;
  }

  async createUser(userData: { email?: string; phone?: string; firstName: string; lastName: string; passwordHash: string }): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async updateLastLogin(userId: string): Promise<void> {
    await db.update(users).set({ lastLoginAt: new Date() }).where(eq(users.id, userId));
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).where(eq(users.isActive, true)).orderBy(desc(users.createdAt));
  }

  async updateUserRole(userId: string, role: UserRole): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ role, updatedAt: new Date() })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async approveUser(userId: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ isApproved: true, updatedAt: new Date() })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async getPendingUsers(): Promise<User[]> {
    return await db.select().from(users).where(eq(users.isApproved, false));
  }

  // Polling center operations
  async getPollingCenters(): Promise<PollingCenter[]> {
    return await db.select().from(pollingCenters).where(eq(pollingCenters.isActive, true));
  }

  async getPollingCenter(id: string): Promise<PollingCenter | undefined> {
    const [center] = await db.select().from(pollingCenters).where(eq(pollingCenters.id, id));
    return center;
  }

  async createPollingCenter(center: InsertPollingCenter): Promise<PollingCenter> {
    const [newCenter] = await db.insert(pollingCenters).values(center).returning();
    return newCenter;
  }

  // Candidate operations
  async getCandidates(): Promise<Candidate[]> {
    return await db.select().from(candidates).where(eq(candidates.isActive, true));
  }

  async createCandidate(candidate: InsertCandidate): Promise<Candidate> {
    const [newCandidate] = await db.insert(candidates).values(candidate).returning();
    return newCandidate;
  }

  // Result operations
  async getResults(): Promise<ResultWithRelations[]> {
    return await db
      .select()
      .from(results)
      .leftJoin(pollingCenters, eq(results.pollingCenterId, pollingCenters.id))
      .leftJoin(users, eq(results.submittedBy, users.id))
      .orderBy(desc(results.createdAt))
      .then(rows => 
        rows.map(row => ({
          ...row.results,
          pollingCenter: row.polling_centers!,
          submitter: row.users!,
          verifier: undefined,
          files: []
        }))
      );
  }

  async getResultsByStatus(status: ResultStatus): Promise<ResultWithRelations[]> {
    return await db
      .select()
      .from(results)
      .leftJoin(pollingCenters, eq(results.pollingCenterId, pollingCenters.id))
      .leftJoin(users, eq(results.submittedBy, users.id))
      .where(eq(results.status, status))
      .orderBy(desc(results.createdAt))
      .then(rows => 
        rows.map(row => ({
          ...row.results,
          pollingCenter: row.polling_centers!,
          submitter: row.users!,
          verifier: undefined,
          files: []
        }))
      );
  }

  async getResultsByPollingCenter(pollingCenterId: string): Promise<ResultWithRelations[]> {
    return await db
      .select()
      .from(results)
      .leftJoin(pollingCenters, eq(results.pollingCenterId, pollingCenters.id))
      .leftJoin(users, eq(results.submittedBy, users.id))
      .where(eq(results.pollingCenterId, pollingCenterId))
      .orderBy(desc(results.createdAt))
      .then(rows => 
        rows.map(row => ({
          ...row.results,
          pollingCenter: row.polling_centers!,
          submitter: row.users!,
          verifier: undefined,
          files: []
        }))
      );
  }

  async createResult(result: InsertResult): Promise<Result> {
    // Calculate total votes from all categories
    let totalVotes = result.invalidVotes;

    if (result.presidentialVotes) {
      const presidentialTotal = Object.values(result.presidentialVotes as Record<string, number>).reduce((sum, votes) => sum + votes, 0);
      totalVotes += presidentialTotal;
    }

    if (result.mpVotes) {
      const mpTotal = Object.values(result.mpVotes as Record<string, number>).reduce((sum, votes) => sum + votes, 0);
      totalVotes += mpTotal;
    }

    if (result.councilorVotes) {
      const councilorTotal = Object.values(result.councilorVotes as Record<string, number>).reduce((sum, votes) => sum + votes, 0);
      totalVotes += councilorTotal;
    }

    const [newResult] = await db
      .insert(results)
      .values({ ...result, totalVotes })
      .returning();
    return newResult;
  }

  async updateResultStatus(
    resultId: string, 
    status: ResultStatus, 
    verifiedBy?: string, 
    flaggedReason?: string
  ): Promise<Result> {
    const updateData: any = { 
      status, 
      updatedAt: new Date() 
    };

    if (verifiedBy) {
      updateData.verifiedBy = verifiedBy;
      updateData.verifiedAt = new Date();
    }

    if (flaggedReason) {
      updateData.flaggedReason = flaggedReason;
    }

    const [updatedResult] = await db
      .update(results)
      .set(updateData)
      .where(eq(results.id, resultId))
      .returning();
    return updatedResult;
  }

  // Result file operations
  async createResultFile(file: InsertResultFile): Promise<ResultFile> {
    const [newFile] = await db.insert(resultFiles).values(file).returning();
    return newFile;
  }

  async getResultFiles(resultId: string): Promise<ResultFile[]> {
    return await db.select().from(resultFiles).where(eq(resultFiles.resultId, resultId));
  }

  // Statistics
  async getStats(): Promise<{
    totalCenters: number;
    resultsReceived: number;
    verified: number;
    flagged: number;
    completionRate: number;
    verificationRate: number;
  }> {
    const [totalCentersResult] = await db
      .select({ count: count() })
      .from(pollingCenters)
      .where(eq(pollingCenters.isActive, true));

    const [resultsReceivedResult] = await db
      .select({ count: count() })
      .from(results);

    const [verifiedResult] = await db
      .select({ count: count() })
      .from(results)
      .where(eq(results.status, 'verified'));

    const [flaggedResult] = await db
      .select({ count: count() })
      .from(results)
      .where(eq(results.status, 'flagged'));

    const totalCenters = totalCentersResult.count;
    const resultsReceived = resultsReceivedResult.count;
    const verified = verifiedResult.count;
    const flagged = flaggedResult.count;

    return {
      totalCenters,
      resultsReceived,
      verified,
      flagged,
      completionRate: totalCenters > 0 ? (resultsReceived / totalCenters) * 100 : 0,
      verificationRate: resultsReceived > 0 ? (verified / resultsReceived) * 100 : 0,
    };
  }

  // Audit operations
  async createAuditLog(log: InsertAuditLog): Promise<AuditLog> {
    const [newLog] = await db.insert(auditLogs).values(log).returning();
    return newLog;
  }

  async getAuditLogs(limit = 100): Promise<AuditLog[]> {
    return await db
      .select()
      .from(auditLogs)
      .orderBy(desc(auditLogs.createdAt))
      .limit(limit);
  }

  // Analytics operations for real-time dashboard
  async getRecentSubmissions(limit: number): Promise<ResultWithRelations[]> {
    return await db.select()
      .from(results)
      .leftJoin(pollingCenters, eq(results.pollingCenterId, pollingCenters.id))
      .leftJoin(users, eq(results.submittedBy, users.id))
      .orderBy(desc(results.createdAt))
      .limit(limit) as any[];
  }

  async getPendingVerifications(): Promise<ResultWithRelations[]> {
    return await db.select()
      .from(results)
      .leftJoin(pollingCenters, eq(results.pollingCenterId, pollingCenters.id))
      .leftJoin(users, eq(results.submittedBy, users.id))
      .where(eq(results.status, 'pending'))
      .orderBy(desc(results.createdAt)) as any[];
  }

  async getTopPerformingCenters(limit: number): Promise<Array<{
    pollingCenter: PollingCenter;
    submissionCount: number;
    verificationRate: number;
  }>> {
    const centerStats = await db.select({
      pollingCenter: pollingCenters,
      submissionCount: count(results.id).as('submissionCount'),
      verifiedCount: sql<number>`COUNT(CASE WHEN ${results.status} = 'verified' THEN 1 END)`.as('verifiedCount'),
    })
      .from(pollingCenters)
      .leftJoin(results, eq(pollingCenters.id, results.pollingCenterId))
      .groupBy(pollingCenters.id)
      .orderBy(desc(count(results.id)))
      .limit(limit);

    return centerStats.map(stat => ({
      pollingCenter: stat.pollingCenter,
      submissionCount: stat.submissionCount,
      verificationRate: stat.submissionCount > 0 ? (stat.verifiedCount / stat.submissionCount) * 100 : 0,
    }));
  }

  async getHourlySubmissionTrends(): Promise<Array<{
    hour: string;
    submissions: number;
    verifications: number;
  }>> {
    const trends = await db.select({
      hour: sql<string>`TO_CHAR(${results.createdAt}, 'YYYY-MM-DD HH24:00')`.as('hour'),
      submissions: count(results.id).as('submissions'),
      verifications: sql<number>`COUNT(CASE WHEN ${results.status} = 'verified' THEN 1 END)`.as('verifications'),
    })
      .from(results)
      .where(sql`${results.createdAt} >= NOW() - INTERVAL '24 hours'`)
      .groupBy(sql`TO_CHAR(${results.createdAt}, 'YYYY-MM-DD HH24:00')`)
      .orderBy(sql`TO_CHAR(${results.createdAt}, 'YYYY-MM-DD HH24:00')`);

    return trends;
  }
}

export const storage = new DatabaseStorage();

