import { useEffect, useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { useWebSocket } from '@/hooks/useWebSocket';
import { useQuery } from '@tanstack/react-query';
import { Activity, Users, CheckCircle, AlertTriangle, Clock, TrendingUp } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';

export function RealTimeAnalytics() {
  const { analytics, isConnected, requestAnalytics } = useWebSocket();
  const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

  // Fetch initial analytics
  const { data: initialAnalytics, isLoading } = useQuery({
    queryKey: ['/api/analytics'],
    refetchInterval: 30000, // Fallback refresh every 30 seconds
  });

  // Use WebSocket analytics if available, otherwise use query data
  const currentAnalytics = analytics || initialAnalytics || {};

  useEffect(() => {
    if (isConnected) {
      requestAnalytics();
      setLastUpdate(new Date());
    }
  }, [isConnected, requestAnalytics]);

  useEffect(() => {
    if (analytics) {
      setLastUpdate(new Date());
    }
  }, [analytics]);

  if (isLoading && !currentAnalytics) {
    return (
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        {[...Array(4)].map((_, i) => (
          <Card key={i} data-testid={`analytics-skeleton-${i}`}>
            <CardHeader className="space-y-0 pb-2">
              <div className="h-4 bg-muted animate-pulse rounded" />
            </CardHeader>
            <CardContent>
              <div className="h-8 bg-muted animate-pulse rounded mb-2" />
              <div className="h-3 bg-muted animate-pulse rounded w-2/3" />
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  const stats = currentAnalytics?.overview;

  return (
    <div className="space-y-6" data-testid="real-time-analytics">
      {/* Connection Status */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <div className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-500 animate-pulse' : 'bg-red-500'}`} />
          <span className="text-sm text-muted-foreground">
            {isConnected ? 'Live Updates Active' : 'Connecting...'}
          </span>
        </div>
        <div className="text-xs text-muted-foreground">
          Last updated: {formatDistanceToNow(lastUpdate)} ago
        </div>
      </div>

      {/* Overview Stats */}
      {stats && (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          <Card data-testid="stat-total-centers">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Centers</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.totalCenters}</div>
              <div className="flex items-center gap-2 text-xs text-muted-foreground">
                <Progress value={stats.completionRate} className="w-full h-1" />
                <span>{stats.completionRate.toFixed(1)}% reporting</span>
              </div>
            </CardContent>
          </Card>

          <Card data-testid="stat-results-received">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Results Received</CardTitle>
              <Activity className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.resultsReceived}</div>
              <p className="text-xs text-muted-foreground">
                +{(currentAnalytics as any)?.recentActivity?.length || 0} in last hour
              </p>
            </CardContent>
          </Card>

          <Card data-testid="stat-verified">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Verified</CardTitle>
              <CheckCircle className="h-4 w-4 text-green-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{stats.verified}</div>
              <div className="flex items-center gap-2 text-xs text-muted-foreground">
                <Progress value={stats.verificationRate} className="w-full h-1" />
                <span>{stats.verificationRate.toFixed(1)}% verified</span>
              </div>
            </CardContent>
          </Card>

          <Card data-testid="stat-pending">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Pending Review</CardTitle>
              <Clock className="h-4 w-4 text-yellow-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-yellow-600">
                {(currentAnalytics as any)?.pendingVerifications || 0}
              </div>
              <p className="text-xs text-muted-foreground">
                {stats.flagged} flagged for attention
              </p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Charts Section */}
      <div className="grid gap-6 md:grid-cols-2">
        {/* Submission Trends */}
        {(currentAnalytics as any)?.submissionTrends && (
          <Card data-testid="submission-trends-chart">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <TrendingUp className="h-5 w-5" />
                24-Hour Submission Trends
              </CardTitle>
              <CardDescription>Submissions and verifications by hour</CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={(currentAnalytics as any).submissionTrends}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis 
                    dataKey="hour" 
                    tickFormatter={(value) => new Date(value).toLocaleTimeString([], {hour: '2-digit'})}
                  />
                  <YAxis />
                  <Tooltip 
                    labelFormatter={(value) => new Date(value).toLocaleString()}
                  />
                  <Line 
                    type="monotone" 
                    dataKey="submissions" 
                    stroke="#8884d8" 
                    strokeWidth={2}
                    name="Submissions"
                  />
                  <Line 
                    type="monotone" 
                    dataKey="verifications" 
                    stroke="#82ca9d" 
                    strokeWidth={2}
                    name="Verifications"
                  />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        )}

        {/* Top Performing Centers */}
        {(currentAnalytics as any)?.topCenters && (
          <Card data-testid="top-centers-chart">
            <CardHeader>
              <CardTitle>Top Performing Centers</CardTitle>
              <CardDescription>Centers with highest submission rates</CardDescription>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={(currentAnalytics as any).topCenters}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis 
                    dataKey="pollingCenter.name" 
                    tick={{ fontSize: 12 }}
                    interval={0}
                    angle={-45}
                    textAnchor="end"
                  />
                  <YAxis />
                  <Tooltip />
                  <Bar 
                    dataKey="submissionCount" 
                    fill="#8884d8" 
                    name="Submissions"
                  />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Recent Activity Feed */}
      {(currentAnalytics as any)?.recentActivity && (
        <Card data-testid="recent-activity-feed">
          <CardHeader>
            <CardTitle>Recent Activity</CardTitle>
            <CardDescription>Live feed of latest submissions and verifications</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4 max-h-96 overflow-y-auto">
              {((currentAnalytics as any).recentActivity || []).slice(0, 10).map((activity: any, index: number) => (
                <div 
                  key={activity.id || index} 
                  className="flex items-center justify-between border-b pb-2"
                  data-testid={`activity-item-${index}`}
                >
                  <div className="flex items-center gap-3">
                    <div className="flex-shrink-0">
                      {activity.status === 'verified' && (
                        <CheckCircle className="h-4 w-4 text-green-600" />
                      )}
                      {activity.status === 'pending' && (
                        <Clock className="h-4 w-4 text-yellow-600" />
                      )}
                      {activity.status === 'flagged' && (
                        <AlertTriangle className="h-4 w-4 text-red-600" />
                      )}
                    </div>
                    <div className="min-w-0 flex-1">
                      <p className="text-sm font-medium truncate">
                        {activity.pollingCenter?.name || 'Unknown Center'}
                      </p>
                      <p className="text-xs text-muted-foreground">
                        {activity.candidate?.name || 'Unknown Candidate'} - {activity.votes} votes
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2 text-right">
                    <Badge 
                      variant={
                        activity.status === 'verified' ? 'default' : 
                        activity.status === 'flagged' ? 'destructive' : 'secondary'
                      }
                    >
                      {activity.status}
                    </Badge>
                    <span className="text-xs text-muted-foreground">
                      {formatDistanceToNow(new Date(activity.createdAt))} ago
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/recent-submissions.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Clock, AlertTriangle, Check } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface RecentSubmissionsProps {
  results?: any[];
}

export default function RecentSubmissions({ results }: RecentSubmissionsProps) {
  // Get the 5 most recent submissions
  const recentResults = results?.slice(0, 5) || [];

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending':
        return <Clock className="h-4 w-4 text-yellow-600" />;
      case 'flagged':
        return <AlertTriangle className="h-4 w-4 text-red-600" />;
      case 'verified':
        return <Check className="h-4 w-4 text-green-600" />;
      default:
        return <Clock className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'pending':
        return 'status-pending';
      case 'flagged':
        return 'status-flagged';
      case 'verified':
        return 'status-verified';
      case 'rejected':
        return 'status-rejected';
      default:
        return '';
    }
  };

  return (
    <Card className="border shadow-sm">
      <CardHeader className="border-b">
        <CardTitle data-testid="text-recent-submissions-title">Recent Submissions</CardTitle>
        <p className="text-sm text-gray-600">Latest result submissions requiring review</p>
      </CardHeader>
      <CardContent className="p-6">
        {recentResults.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            No recent submissions
          </div>
        ) : (
          <div className="space-y-4">
            {recentResults.map((result: any) => (
              <div key={result.id} className="flex items-start space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="flex-shrink-0">
                  <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center">
                    {getStatusIcon(result.status)}
                  </div>
                </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <p className="text-sm font-medium text-gray-900" data-testid={`submission-center-${result.id}`}>
                      {result.pollingCenter?.code || 'Unknown Center'}
                    </p>
                    <span className="text-xs text-gray-500" data-testid={`submission-time-${result.id}`}>
                      {formatDistanceToNow(new Date(result.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                  <p className="text-sm text-gray-600" data-testid={`submission-submitter-${result.id}`}>
                    Submitted by {result.submitter?.firstName} {result.submitter?.lastName}
                  </p>
                  <div className="flex items-center space-x-2 mt-1">
                    <Badge className={getStatusBadgeClass(result.status)} data-testid={`submission-status-${result.id}`}>
                      {result.status.charAt(0).toUpperCase() + result.status.slice(1)}
                    </Badge>
                    <span className="text-xs text-gray-500">
                      {result.submissionChannel}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="mt-4">
          <Button
            variant="outline"
            className="w-full"
            data-testid="button-view-all-submissions"
          >
            View All Submissions
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

